#define START_ADDR 0x0000

void write_flash(uint32_t page, uint8_t *data, size_t size) {
	page = START_ADDR & ~(SPM_PAGESIZE - 1);
	
	for (uint16_t i = 0; i < ((size - 1) / SPM_PAGESIZE) + 1; i++) {
		boot_page_erase(page + SPM_PAGESIZE * i);
		boot_spm_busy_wait();
		
		for (uint16_t j = 0; j < SPM_PAGESIZE; j += 2)
		boot_page_fill(page + SPM_PAGESIZE * i + j, 0xFFFF);
		
		for (uint16_t j = 0; j < SPM_PAGESIZE; j += 2) {
			uint32_t curr_index = (SPM_PAGESIZE * i) + j;
			
			uint16_t word;
			
			if (curr_index < size) {
				word = data[curr_index] | (data[curr_index+1] << 8);
				} else {
				word = 0xFFFF;
			}
			
			boot_page_fill(page + SPM_PAGESIZE * i + j, word);
		}
		
		boot_page_write(page + SPM_PAGESIZE * i);
		boot_spm_busy_wait();
	}
	
	boot_rww_enable();
}

int main(void)
{
	SPI_init();
	//const char buffer[] = {'a', 'b', 'c', '\0'};
	//sei();
	//UART_init(UBBR);
	//print(buffer);
	
	// temporary write to the flash
	//W25QXX_write_app(data, 1536);
	//W25QXX_write_remainder(0x00);
	
	// read first page
	
	DDRB = 0xFF;
	for (int i = 0; i < 2; i++) {
		PORTB ^= (1 << PB7);
		_delay_ms(500);
	}
	
	uint8_t buf[256] = {0x67};
	
	W25QXX_read_stream(0x00000000, buf, 256);
	
	cli(); // always remember to disable interrupts when writing to flash
	write_flash(START_ADDR, buf, 256);
	
	//asm("JMP 0x0");
	
	for (int i = 0; i < 10; i++) {
		PORTB ^= (1 << PB7);
		_delay_ms(500);
	}

	while (1) ; // should be unreachable
}
